-- ----------------------------
-- Table structure for certificate_details
-- ----------------------------
DROP TABLE IF EXISTS `certificate_details`;
CREATE TABLE `certificate_details`  (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT,
  `h1` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `p1` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `v1` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `h2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `p2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `v2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `li` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `certificate_id` bigint UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `certificate_details_certificate_id_foreign`(`certificate_id` ASC) USING BTREE,
  CONSTRAINT `certificate_details_certificate_id_foreign` FOREIGN KEY (`certificate_id`) REFERENCES `certificates` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 24 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of certificate_details
-- ----------------------------
INSERT INTO `certificate_details` VALUES (8, 'DevOps Certifications', 'Looking for smoother workflows and faster deployment? BCS DevOps certification offers the ideal introduction for individuals and organisations making the move to the DevOps way of working.', '/storage/20220105_143650.mp4', 'DevOps Certification Portfolio', NULL, NULL, 'Get your DevOps journey off to the best start by understanding the fundamental DevOps values methods and techniques.,The BCS Foundation Level Certificate in DevOps provides framework-agnostic training with core DevOps values at the heart of every discussion supported by practical examples bringing the values to life.,The foundation certificate is just the first in our new series of certifications creating a complete BCS DevOps learning pathway. Coming soon: Practitioner level.', 4, '2022-01-05 14:36:50', '2022-01-05 14:36:50');
INSERT INTO `certificate_details` VALUES (14, 'GDPR and Data Protection Certifications', 'Build your knowledge of how GDPR and Data Protection will affect your business, helping you to ensure your organisation will be compliant. Make sure you understand the new rules and equip yourself to be a subject matter expert.', '/storage/20220106_120249.mp4', 'GDPR & Data Protection Certification Portfolio', NULL, NULL, NULL, 5, '2022-01-06 12:02:49', '2022-01-06 12:02:49');
INSERT INTO `certificate_details` VALUES (15, 'Information Security and CCP Scheme Certifications', 'Aligned to the ISO/IEC 27000 series and BS 25999 standards, IT professionals can start with our broad based foundation course and progress to more subject focused practitioner certifications.', '/storage/20220106_122110.mp4', 'Information Security & CCP Assured Service', NULL, NULL, NULL, 6, '2022-01-06 12:21:10', '2022-01-06 12:21:10');
INSERT INTO `certificate_details` VALUES (16, 'Agile Certifications', 'This comprehensive Agile certification programme provides a complete Agile learning pathway. It sets an industry benchmark that supports Agile transformation to deliver value across your organisation straight away.', '/storage/20220107_094644.mp4', 'Agile Certification Portfolio', NULL, NULL, NULL, 1, '2022-01-07 09:46:44', '2022-01-07 09:46:44');
INSERT INTO `certificate_details` VALUES (17, 'Artificial Intelligence (AI) Certifications', 'AI is already pervasive across many sectors. The vast majority of software and systems already incorporate elements of AI, in its broadest sense, specifically in regard to machine learning.', '/storage/20220107_095351.mp4', 'Artificial Intelligence (AI) Certifications Portfolio', NULL, NULL, NULL, 2, '2022-01-07 09:53:51', '2022-01-07 09:53:51');
INSERT INTO `certificate_details` VALUES (18, 'Business Analysis Certifications', 'Attain a leading business analysis certification and demonstrate that you’ve met the high standards set by BCS, The Chartered Institute for IT. Over 100,000 professionals worldwide are certified with BCS.', '/storage/20220107_100625.mp4', 'Business Analysis Certifications Portfolio', NULL, NULL, 'Develop your skills to support successful business change programmes,Gain an internationally recognised certification enabling you to take your skills around the world,Become an Associate Member for free on gaining a certification and enjoy all the benefits of membership,Map your skills and identify your goals along your career path by following the SFIAplus industry-standard framework,', 3, '2022-01-07 10:06:25', '2022-01-07 10:06:25');
INSERT INTO `certificate_details` VALUES (19, 'IT Asset Management Certifications', 'Asset management is becoming increasingly important to organisations and an absolutely vital tool in managing often uncontrollable costs. Qualified and experienced asset managers are highly valued by their organisations.', '/storage/20220107_104659.mp4', 'IT Asset Management Certification Portfolio', NULL, NULL, NULL, 7, '2022-01-07 10:46:59', '2022-01-07 10:46:59');
INSERT INTO `certificate_details` VALUES (20, 'IT Service Management Certification', 'IT service management (known as ITSM) is the craft of implementing, managing, and delivering IT services to meet the needs of an organization. It ensures that the appropriate mix of people, processes, and technology are in place to provide value.', NULL, NULL, NULL, NULL, NULL, 8, '2022-01-07 10:51:53', '2022-01-07 10:51:53');
INSERT INTO `certificate_details` VALUES (21, 'Open Stack Software Certification', 'OpenStack is an open source platform that uses pooled virtual resources to build and manage private and public clouds. The tools that comprise the OpenStack platform, called \"projects,\" handle the core cloud-computing services of compute, networking, storage, identity, and image services', NULL, NULL, NULL, NULL, NULL, 17, '2022-01-07 10:53:21', '2022-01-07 10:53:21');
INSERT INTO `certificate_details` VALUES (22, 'Project Management', 'Our project management portfolio provides comprehensive support for individuals and organisations around the world.', '/storage/20220107_105453.mp4', 'Project and Programme Management and Consultancy Certification Portfolio', NULL, NULL, NULL, 18, '2022-01-07 10:54:53', '2022-01-07 10:54:53');
INSERT INTO `certificate_details` VALUES (23, 'User Experience (UX) Certifications', 'Designed to ensure candidates have suitable knowledge of the core concepts of user experience and are aware of the most common tools, techniques and methods used in the field.', '/storage/20220107_105727.mp4', 'User Experience (UX) Certification Portfolio', NULL, NULL, NULL, 19, '2022-01-07 10:57:27', '2022-01-07 10:57:27');

