INSERT INTO `certificate_details_accordians` VALUES (2, '8', 'DevOps Certification Portfolio', 'Foundation', NULL, 'pagelist?BCS%20Foundation%20Level%20Certificate%20in%20DevOps## BCS Foundation Level Certificate in DevOps,', '', 'SFIAplus Level 1-3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-06 10:27:47', '2022-01-06 10:27:47');
INSERT INTO `certificate_details_accordians` VALUES (10, '14', 'GDPR and Data Protection Certification Portfolio', 'Foundation', 'GDPR', 'pagelist?BCS%20Foundation%20Certificate%20in%20Data%20Protection## BCS Foundation Certificate in Data Protection,', '', 'SFIAplus Level 1-2', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people\'s work.', '2022-01-06 12:05:29', '2022-01-06 12:05:29');
INSERT INTO `certificate_details_accordians` VALUES (11, '14', '', 'Practitioner', NULL, 'pagelist?BCS%20Practitioner%20Certificate%20in%20Data%20Protection##BCS Practitioner Certificate in Data Protection,', '', 'SFIAplus Level 3-4', 'At practitioner level you have a good level of operational business skills and can work on a broad range of complex activities.', '2022-01-06 12:17:02', '2022-01-06 12:17:02');
INSERT INTO `certificate_details_accordians` VALUES (12, '15', 'Information Security and CCP Assured Service', 'Essentials', NULL, 'pagelist?BCS%20EXIN%20Essentials%20Certificate%20in%20Blockchain##BCS EXIN Essentials Certificate in Blockchain,', '', 'SFIAplus Level 2', 'At essentials level, you can work under supervision to carry out a range of activities independently, monitor your own work in short timeframes and absorb technical information.', '2022-01-06 12:22:33', '2022-01-06 12:22:33');
INSERT INTO `certificate_details_accordians` VALUES (13, '15', '', 'Foundation', NULL, 'pagelist?BCS%20Foundation%20Certificate%20in%20Information%20Security%20Management%20Principles##BCS Foundation Certificate in Information Security Management Principles,pagelist?BCS%20EXIN%20Foundation%20Certificate%20in%20Blockchain##BCS EXIN Foundation Certificate in Blockchain,', '', 'SFIAplus Level 3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-06 12:24:39', '2022-01-06 12:24:39');
INSERT INTO `certificate_details_accordians` VALUES (14, '15', '', 'Practitioner', NULL, 'pagelist?BCS%20Practitioner%20Certificate%20in%20Business%20Continuity%20Management##BCS Practitioner Certificate in Business Continuity Management,pagelist?BCS%20Practitioner%20Certificate%20in%20Freedom%20of%20Information##BCS Practitioner Certificate in Freedom of Information,pagelist?BCS%20Practitioner%20Certificate%20in%20Information%20Assurance%20Architecture## BCS Practitioner Certificate in Information Assurance Architecture,pagelist?BCS%20Practitioner%20Certificate%20in%20Information%20Assurance%20Architecture##BCS Practitioner Certificate in Information Risk Management,', '', 'SFIAplus Level 4-5', 'At practitioner level you have a good level of operational business skills and can work on a broad range of complex activities.', '2022-01-06 12:27:39', '2022-01-06 12:27:39');
INSERT INTO `certificate_details_accordians` VALUES (15, '15', '', 'Information Assurance Certification', NULL, 'pagelist?NCSC%20Certified%20Cyber%20Professional%20(CCP)%20assured%20service##NCSC Certified Cyber Professional (CCP) assured service,', '', '', '', '2022-01-06 12:30:13', '2022-01-06 12:30:13');
INSERT INTO `certificate_details_accordians` VALUES (16, '16', 'Agile Certification Portfolio', 'Foundation', NULL, 'pagelist?BCS%20Foundation%20Certificate%20in%20Agile##BCS Foundation Certificate in Agile,pagelist?BCS%20EXIN%20Foundation%20Certificate%20in%20Agile%20Scrum##BCS EXIN Foundation Certificate in Agile Scrum,', '', 'SFIAplus Level 3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-07 09:48:22', '2022-01-07 09:48:22');
INSERT INTO `certificate_details_accordians` VALUES (17, '16', '', 'Practitioner', NULL, 'pagelist?BCS%20EXIN%20Practitioner%20Certificate%20in%20Agile%20Scrum%20Master##BCS EXIN Practitioner Certificate in Agile Scrum Master,pagelist?BCS%20EXIN%20Practitioner%20Certificate%20in%20Agile%20Scrum%20Product%20Owner##BCS EXIN Practitioner Certificate in Agile Scrum Product Owner,pagelist?BCS%20EXIN%20Practitioner%20Certificate%20in%20Agile%20Scrum%20Product%20Owner%20(Bridge)##BCS EXIN Practitioner Certificate in Agile Scrum Product Owner (Bridge),', '', 'SFIAplus Level 4', 'At practitioner level you have a good level of operational business skills and can work on a broad range of complex activities.', '2022-01-07 09:51:23', '2022-01-07 09:51:23');
INSERT INTO `certificate_details_accordians` VALUES (18, '17', 'Artificial Intelligence (AI) Certifications Portfolio', 'AI Pathway', NULL, 'pagelist?Artificial%20Intelligence%20Foundation%20Pathway##Artificial Intelligence Foundation Pathway,', '', 'SFIAplus Level 3', 'At this level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-07 09:54:41', '2022-01-07 09:54:41');
INSERT INTO `certificate_details_accordians` VALUES (19, '17', '', 'Essentials', NULL, 'pagelist?BCS%20Essentials%20Certificate%20in%20Artificial%20Intelligence## BCS Essentials Certificate in Artificial Intelligence,', '', 'SFIAplus Level 2', 'At essentials level, you can work under supervision to carry out a range of activities independently, monitor your own work in short timeframes and absorb technical information.', '2022-01-07 09:55:40', '2022-01-07 09:55:40');
INSERT INTO `certificate_details_accordians` VALUES (20, '17', '', 'Foundation', NULL, 'pagelist?BCS%20Foundation%20Certificate%20in%20Artificial%20Intelligence##BCS Foundation Certificate in Artificial Intelligence\n,', '', 'SFIAplus Level 2', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-07 10:04:40', '2022-01-07 10:04:40');
INSERT INTO `certificate_details_accordians` VALUES (21, '18', 'Business Analysis Certifications Portfolio', 'Foundation', 'You’re getting started in business analysis and you want to develop a broad understanding of the latest principles and approaches. Most people begin their business analysis journey here.', 'pagelist?Foundation%20Certificate%20in%20Business%20Analysis## Business Analysis Foundation,pagelist?Foundation%20Certificate%20in%20Business%20Change## Business Change,pagelist?Foundation%20Certificate%20in%20Organisation%20Behaviour##Organisation Behaviour,', '', 'SFIAplus Level 3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-07 10:21:36', '2022-01-07 10:21:36');
INSERT INTO `certificate_details_accordians` VALUES (22, '18', '', 'Practitioner', 'You’ve gained a foundation level certification and you’d like to move on to demonstrate specialist practical skills. You can accumulate modules to gain a BCS International Diploma in Business Analysis too.', 'pagelist?Practitioner%20Certificate%20in%20Business%20Analysis%20Practice##Business Analysis Practice,pagelist?Practitioner%20Certificate%20in%20Benefits%20Management%20and%20Business%20Acceptance##Benefits Management and Business Acceptance,pagelist?Practitioner%20Certificate%20in%20Modelling%20Business%20Processes##Modelling Business Processes,pagelist?Practitioner%20Certificate%20in%20Data%20Management%20Essentials##Data Management Essentials,pagelist?Foundation%20Certificate%20in%20Requirements%20Engineering##Requirements Engineering,', 'pagelist?International%20Diploma%20in%20Business%20Analysis##International Diploma in Business Analysis,', 'SFIAplus Level 4', 'At practitioner level you have a good level of operational business skills and can work on a broad range of complex activities.', '2022-01-07 10:26:58', '2022-01-07 10:26:58');
INSERT INTO `certificate_details_accordians` VALUES (23, '18', '', 'Professional', 'You’re well on the way to becoming a Chartered IT Professional or Business Analysis Expert. You’re looking to gain certifications that show you have developed specialist analytical, business and personal skills. You can accumulate modules to work towards a BCS Advanced International Diploma in Business Analysis too.', 'pagelist?Professional%20Certificate%20in%20Advanced%20Requirements%20Engineering##Advanced Requirements Engineering,pagelist?Professional%20Certificate%20in%20Agile%20Business%20Analysis##Agile Business Analysis,pagelist?Professional%20Certificate%20in%20Benefits%20Planning%20Realisation##Benefits Planning Realisation,pagelist?Professional%20Certificate%20in%20Business%20Architecture##Business Architecture,pagelist?Professional%20Certificate%20in%20Business%20Finance##Business Finance,pagelist?Professional%20Certificate%20in%20Data%20Analysis##Data Analysis,pagelist?Professional%20Certificate%20in%20Stakeholder%20Engagement##Stakeholders Engagement,pagelist?Professional%20Certificate%20in%20Team%20Leadership##Team Leadership,', '', 'SFIAplus Level 5', 'At professional level you are self-sufficient and can influence the company you work for, often creating bold and innovative work.', '2022-01-07 10:39:23', '2022-01-07 10:39:23');
INSERT INTO `certificate_details_accordians` VALUES (24, '18', '', 'Consultant', 'You’re a senior Business Analyst with an extensive range of skills and experience. You’re looking to gain the highest certifications available to demonstrate your professional abilities.', '', 'pagelist?Advanced%20International%20Diploma%20in%20Business%20analysis##Advanced International Diploma in Business analysis,', 'SFIAplus Level 6', 'You’re a senior Business Analyst with an extensive range of skills and experience. You’re looking to gain the highest certifications available to demonstrate your professional abilities.', '2022-01-07 10:43:14', '2022-01-07 10:43:14');
INSERT INTO `certificate_details_accordians` VALUES (25, '18', '', 'Expert', 'You’re a senior Business Analyst looking to gain the highest level of certification to demonstrate your higher skill levels and expertise.', '', 'pagelist?Expert%20BA%20Award##Expert BA Award,', 'SFIAplus Level 7', 'At expert level you have a full range of management and leadership skills and make decisions that are critical to your organisation.', '2022-01-07 10:45:00', '2022-01-07 10:45:00');
INSERT INTO `certificate_details_accordians` VALUES (26, '19', 'IT Asset Management Certification Portfolio', 'Foundation', NULL, 'pagelist?BCS%20EXIN%20Essentials%20Certificate%20in%20Blockchain##BCS Foundation Certificate in Software and Hardware Asset Management (ISAHAM),', '', 'SFIAplus Level 3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-07 10:47:58', '2022-01-07 10:47:58');
INSERT INTO `certificate_details_accordians` VALUES (27, '22', 'Project Management Portfolio', 'Foundation', NULL, 'pagelist?BCS%20Foundation%20Certificate%20in%20IS%20Project%20Management##BCS Foundation Certificate in IS Project Management,', '', 'SFIAplus Level 3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people\'s work.', '2022-01-07 10:55:43', '2022-01-07 10:55:43');
INSERT INTO `certificate_details_accordians` VALUES (28, '23', 'User Experience (UX) Certification Portfolio', 'Foundation', NULL, 'pagelist?BCS%20Foundation%20Certificate%20in%20User%20Experience##BCS Foundation Certificate in User Experience (UX),', '', 'SFIAplus Level 3', 'At foundation level you can carry out a broad range of tasks, use your initiative and schedule your own and other people’s work.', '2022-01-07 10:59:12', '2022-01-07 10:59:12');

