INSERT INTO `higher_page` VALUES (56, 'membership?BCS%20Certificate%20in%20IT##Apply for Membership', 'membership?BCS%20Certificate%20in%20IT##Apply for Membership', '', '', 57, '2022-02-16 10:35:01', '2022-02-16 10:35:01', 'What will I learn?', 'A core module and three selected modules allowing you to tailor your learning.', 'Core modules: ', 'https://www.bcs.org/media/1152/heq-dip-pisp-syllabus.pdf##Professional issues in IS practice syllabus,https://www.bcs.org/media/5846/heq-big-data-management-syllabus.pdf##Big data management syllabus,https://www.bcs.org/media/3752/heq-dip-cn-syllabus.pdf##Computer networks syllabus,https://www.bcs.org/media/1150/heq-dip-ds-syllabus.pdf##Database systems syllabus,https://www.bcs.org/media/7761/heq-dip-pm-syllabus.pdf##IT project management syllabus\n', 'https://www.bcs.org/media/6131/heq-dip-oop-syllabus-1.pdf##Object oriented programming systems syllabus,https://www.bcs.org/media/1153/heq-dip-pit-syllabus.pdf##Principles of internet technologies syllabus,https://www.bcs.org/media/5847/heq-smart-systems-syllabus.pdf##Smart systems syllabus,https://www.bcs.org/media/6132/heq-dip-se-syllabus-1.pdf##Software engineering (part 1) syllabus,https://www.bcs.org/media/1156/heq-dip-sad-syllabus.pdf##Systems analysis and design syllabus,https://www.bcs.org/media/5848/heq-user-experience-syllabus.pdf##User experience syllabus,https://www.bcs.org/media/5849/heq-web-application-development-syllabus.pdf##Web application development syllabus', 2, '');
INSERT INTO `higher_page` VALUES (57, 'membership?BCS%20Certificate%20in%20IT##Apply for Membership', 'membership?BCS%20Certificate%20in%20IT##Apply for Membership', '', '', 58, '2022-02-16 10:35:01', '2022-02-16 10:35:01', 'What will I learn?', 'Four selected modules and a professional IT project.', 'Core modules: ', 'https://www.bcs.org/media/1160/heq-pgd-adms-syllabus.pdf##Advanced database management systems syllabus, https://www.bcs.org/media/1164/heq-pgd-mis-syllabus.pdf##Management information systems syllabus,https://www.bcs.org/media/1170/heq-pgd-se2-syllabus.pdf##Software engineering (part 2) syllabus,https://www.bcs.org/media/1161/heq-pgd-csm-syllabus.pdf##Computer services management syllabus,https://www.bcs.org/media/7762/heq-pgd-nis-syllabus.pdf##Network information systems syllabus,https://www.bcs.org/media/1166/heq-pgd-pp-syllabus.pdf##Programming paradigms syllabus,https://www.bcs.org/media/1169/heq-pgd-sdm-syllabus.pdf##System design methods syllabus,https://www.bcs.org/media/1171/heq-pgd-we-syllabus.pdf##Web engineering syllabus,https://www.bcs.org/media/7772/heq-pgd-ite-syllabus.pdf##IT and the environment syllabus', NULL, 3, '');

