INSERT INTO `link_details` VALUES (29, 'What modules do I need?', 'Core modules - you must pass both of these', 'pagelist?Practitioner%20Certificate%20in%20Business%20Analysis%20Practice##Business Analysis Practice,pagelist?Foundation%20Certificate%20in%20Requirements%20Engineering##Requirements Engineering', 'Knowledge modules - you must pass one of these', 'pagelist?Foundation%20Certificate%20in%20Business%20Analysis##Business Analysis Foundation, pagelist?Foundation%20Certificate%20in%20Business%20Change##Business Change, pagelist?BCS%20Foundation%20Certificate%20in%20IS%20Project%20Management##IS Project Management, pagelist?Foundation%20Certificate%20in%20Organisation%20Behaviour##Organisation Behaviour\n', 'Practitioner modules - you must pass both of these', 'pagelist?Practitioner%20Certificate%20in%20Modelling%20Business%20Processes##Modelling Business Processes,pagelist?Practitioner%20Certificate%20in%20Data%20Management%20Essentials##Data Management Essentials,pagelist?BCS%20Practitioner%20Certificate%20in%20Systems%20Modelling%20Techniques##Systems Modelling Techniques,pagelist?BCS%20Practitioner%20Certificate%20in%20Systems%20Development%20Essentials##Systems Development Essentials, pagelist?Practitioner%20Certificate%20in%20Benefits%20Management%20and%20Business%20Acceptance##Benefits Management and Business Acceptance', '2022-02-07 12:15:53', '2022-02-07 12:15:53', 53);

