-- Records of page_certificate_details
-- ----------------------------
INSERT INTO `page_certificate_details` VALUES (2, 'BCS Foundation Certificate in Agile', 'Gain a solid understanding of the concepts and values of Agile by challenging conventional methods and thinking.', 'This Agile certificate will answer the questions: What is Agile? What is Agile working? And how can I embed Agile into my organisation? This certification will equip candidates with knowledge that can be applied in a variety of situations and promotes effective working within any Agile team.', NULL, 'Who is it for?', NULL, 'Business and IT professionals who are interested in Agile practices and the Agile methodology, Anyone requiring an understanding of Agile as well as organisational leaders and managers wanting to understand the value of Agile practices,Those who work in an Agile environment including software testers/developers/product owners/business analysts and Scrum masters', 'Entry Requirements', NULL, 'There are no entry requirements for this certification., Training with a BCS accredited training provider is recommended.', 'What will I learn?', 'You’ll gain a broad understanding of agile including:', 'Why Agile? The factors that drive Agile adoption,The Agile Manifesto in detail,The Agile mindset,Roles in Agile teams,Common Agile practices,The practical applications of Agile', 'Useful Resources', 'Visit the BCS Bookshop to find Agile books and resources.\n\nHave a read of these Agile blogs:', 'Improve business processes with agile,Agile on a large scale', 'How do I get this certification?', 'Take the complete training programme (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Agile.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £155 + VAT - though prices may vary depending on training provider.', ' What format is the exam?', 'One hour ‘closed book’ with 40 multiple choice questions, Pass mark is 65% (26/40)', 2, '2022-01-21 10:46:49', '2022-01-21 10:46:49', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (3, 'BCS EXIN Foundation Certificate in Agile Scrum', 'Gain a unique certification that combines Agile service management techniques and Scrum development frameworks.', 'A unique learning pathway designed to support a holistic approach to effectively embed Agile practices across an organisation. This Agile certificate provides the widest curriculum from Agile and Lean, and offers the knowledge, tools and capability for people to better manage change and deliver products and services.', NULL, 'Who is it for?', NULL, 'Project managers / software developers / product managers / IT service managers and business managers who are interested in Agile practices,Anyone requiring an understanding of Agile Scrum as well as organisational leaders and managers wanting to understand the value of Agile Scrum practices or those who work in an Agile environment.', ' Entry Requirements', NULL, 'There are no entry requirements for this certification. Training with a BCS accredited training provider is recommended.', ' What will I learn?', 'You’ll gain a broad understanding of:', 'The Agile Mway of thinking,Scrum practices,Scrum planning and estimation\n,Monitoring Scrum projects,Advanced Scrum projects', ' Useful Resources', 'Visit the BCS Bookshop to find Agile books and resources.               Have a read of these Agile blogs:', 'Improve business processes with agile,Agile on a large scale', 'How do I get this certification?', 'Take the complete training programme (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.\n\n', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Agile.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £155 + VAT - though prices may vary depending on training provider.', ' What format is the exam?', 'One hour ‘closed book’ with 40 multiple choice questions,Pass mark is 65% (26/40)', 3, '2022-01-21 11:51:23', '2022-01-21 11:51:23', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (4, 'BCS EXIN Practitioner Certificate in Agile Scrum Master', 'Learn all you need to successfully adopt and facilitate Agile or Scrum in your workplace with this practitioner certificate.', 'The Agile Scrum pathway is designed to support a holistic approach to effectively embed agile practices across an organisation. It provides the knowledge, tools and capability for people to better manage change and deliver products and services.', NULL, 'Who is it for?', NULL, 'Agile Scrum Master / Agile Product Owner / Internal Coach / Development Leadership (VP / Director / Manager) / Project Programme Manager / Development Team Member (Architect Developer QA Tester UI or UX Designer) And those who are actively engaged in Agile Scrum environments.', 'Entry Requirements', NULL, 'Completion of the BCS EXIN Foundation Certificate in Agile Scrum is strongly recommended, Attendance at an accredited BCS EXIN Agile Scrum Master training course is mandatory.', 'What will I learn?', NULL, 'The Agile Mway of thinking,The Scrum Master role,Agile estimating / planning / monitoring and control,Complex projects,Adopting Agile', 'Useful Resources', 'Visit the BCS Bookshop to find Agile books and resources.\n\nHave a read of these Agile blogs:', 'Improve business processes with agile,Agile on a large scale,The financial management of Agile projects', 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.\n\n', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What format is the exam?', '90 minute ‘closed book’  Pass mark is 65% (26/40)exam with 40 multiple choice questions', 4, '2022-01-21 12:04:47', '2022-01-21 12:04:47', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (5, 'BCS EXIN Practitioner Certificate in Agile Scrum Product Owner', 'Gain a certificate that provides product owners with knowledge and skills of the Agile framework and Scrum methodology - ensuring you can deliver maximum value for the customer and value for your business.', 'The product owner role focuses on bringing value for the customer and value for the business, through project management techniques.', NULL, 'Who is it for?', NULL, 'Agile product owners / product managers / internal coach / project / programme manager / development team member / architect / developer / QA tester / UI or UX designer operating in an Agile Scrum environment.', 'Entry Requirements', NULL, 'Candidates are required to complete the BCS EXIN Foundation Certificate in Agile Scrum, Attendance at an accredited BCS EXIN Agile Scrum Product Owner training course is mandatory.', 'What will I learn?', 'You will gain a broad understanding of:', 'The Agile Mway of thinking, The Product Owner role, Managing the product backlog, Complex projects, Adding value to projects and programmes', ' Useful Resources', 'Visit the BCS Bookshop to find Agile books and resources.\n\nHave a read of these Agile blogs:', 'Improve business processes with agile, Agile on a large scale, The financial management of Agile projects', 'How do I get this certification?', 'Take the complete training programme (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.\n\n', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, ' What format is the exam?', '90 minute ‘closed book’ exam with 40 multiple choice questions, Pass mark is 65% (26/40)', 5, '2022-01-21 12:15:57', '2022-01-21 12:15:57', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (6, 'BCS EXIN Practitioner Certificate in Agile Scrum Product Owner (Bridge)', 'Transition from a certified Scrum Master to an Agile Product Owner with this tailored ‘bridging course’ designed to confirm skills and knowledge of the Agile framework and Scrum methodology, specifically with the Product Owner role in mind.', NULL, NULL, 'Who is it for?', NULL, 'Project managers / software developers / product managers / IT service managers and business managers who already hold the BCS EXIN Agile Scrum Master certificate and would like to transition to become an Agile Product Owner.', 'Entry Requirements', NULL, 'Candidates are required to already hold the BCS EXIN Agile Scrum Master Certificate or Advanced Certified ScrumMaster™ (A-CSM).', 'What will I learn?', 'You will be able to demonstrate the ability to analyse, understand and explain Agile concepts in the following areas:', 'The Agile Mway of thinking, The Product Owner role, Managing the product backlog, Complex projects, Adding value to projects and programmes', ' Useful Resources', 'Visit the BCS Bookshop to find Agile books and resources.\n\nHave a read of these Agile blogs:', 'Improve business processes with agile, Agile on a large scale, The financial management of Agile projects', 'How do I get this certification?', 'Take the complete training programme (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.\n\n', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What format is the exam?', '90 minute ‘closed book’ exam with 40 multiple choice questions, Pass mark is 65% (26/40)', 6, '2022-01-21 13:23:14', '2022-01-21 13:23:14', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (7, 'BCS Essentials Certificate in Artificial Intelligence', 'Take your first steps in AI training to understand the principles of AI, its benefits and risks, and the processes behind machine learning.', NULL, NULL, 'Who is it for?', NULL, 'Individuals with an interest in (or a need to implement) AI in an organisation / especially those working in areas such as science/ engineering / knowledge engineering / finance or IT services, Middle and senior managers running or assembling teams to create AI dependent applications and services that need to understand the context of AI in an industrial setting.', 'Entry Requirements', '', 'There are no entry requirements for this certification. Training with a BCS accredited training provider is highly recommended.', 'What will I learn?\n', 'Develop your knowledge and understanding of:', 'the terminology and general principles, including benefits and types of AI, the basic process of machine learning (ML), the challenges and risks associated with an AI project, the future of AI and humans in work', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.\n\n', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Agile.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £155 + VAT - though prices may vary depending on training provider.', ' What format is the exam?', '30 minute ‘closed book’ with 20 multiple choice questions, \nPass mark is 65% (13/20)', 7, '2022-01-21 13:55:21', '2022-01-21 13:55:21', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (8, 'BCS Foundation Certificate in Artificial Intelligence', 'Take your first step on your business analysis career path by attaining a certification that employers around the world respect and value\nUnderstand how to identify and evaluate options for improving your business', NULL, NULL, 'Who is it for?', NULL, 'Engineers / scientists / organisational change practitioners / service architects / program and planning managers / web developers /  chief technical officers / service provider portfolio strategists / leads / business strategists and consultants, Anyone with an interest in (or need to implement) artificial intelligence in an organisation especially those working in areas such as science/ engineering / knowledge engineering / finance/ education or IT services.', 'Entry Requirements', NULL, 'There are no entry requirements for this certification. Training with a BCS accredited training provider is recommended.', 'What will I learn?', 'You will gain a broad understanding of:', 'Ethical and sustainable human and artificial intelligence,\nArtificial Intelligence and robotics,\nApplying the benefits of AI - challenges and risks,\nStarting AI: how to build a Machine Learning toolbox - theory and practice,\nThe management role and responsiblities of humans and machines', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.\n\n', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Agile.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £155 + VAT - though prices may vary depending on training provider.', ' What format is the exam?', 'One hour ‘closed book’ exam with 40 multiple choice questions, Pass mark is 65% (26/40)', 8, '2022-01-21 14:05:26', '2022-01-21 14:05:26', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (11, 'Foundation Certificate in Business Analysis', NULL, '', 'Take your first step on your business analysis career path by attaining a certification that employers around the world respect and value, Understand how to identify and evaluate options for improving your business, Develop skills and knowledge to support successful business change programmes within your organisation, Attain one of the four modules you need to gain the International Diploma in Business Analysis\n\n', 'Who is it for?', NULL, 'The Foundation Certificate is designed for people who want to understand business analysis support change and improve business processes,\nSuitable for business analysts business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', ' What will I learn?', 'You’ll gain a broad understanding of business analysis, including:', 'The role and competencies of a Business Analyst,\nHow to identify and evaluate options for improving your business,\nInvestigative techniques including interviews/ workshops/prototyping and scenarios,\nHow to categorise analyse and manage stakeholders,\nMaking a business case and assessing feasibility,\nHow to document manage and validate requirements', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At an approved provider near you and\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £192(£160 + VAT)', 'What format is the exam?', 'One hour ‘closed book’ with 40 multiple choice questions,\nPass mark is 65% (26/40)', 9, '2022-01-24 11:20:10', '2022-01-24 11:20:10', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (12, 'Foundation Certificate in Business Change', NULL, NULL, 'Gain an entry-level qualification by understanding the processes and techniques used in delivering business change, Develop skills and knowledge to support successful business change programmes within your organisation, Understand how to design business changes effectively including the organisational people information technology and process aspects, Attain one of the four modules you need to gain the International Diploma in Business Analysis', 'Who is it for?', NULL, 'The Foundation certificate is for people who want to gain a deep understanding of the many processes/framework and techniques needed to implement successful business change,\nSuitable for business analysts, business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business change, including:', 'An appreciation of the principles processes roles and approaches involved in business change,\nHow to use information technology to enable business change and as a driver for business change,\nHow to align the organisation with external and internal influences,\nHow to implement business change effectively', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\n\n', 'Where can I study?', 'At an approved provider near you and\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £192(£160 + VAT)', NULL, NULL, 10, '2022-01-24 11:30:17', '2022-01-24 11:30:17', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (13, 'Foundation Certificate in Organisation Behaviour', NULL, NULL, 'Underpin your understanding of business finance and organisational behaviour, Gain the commercial and organisational knowledge you need to deliver effective business analysis, Understand external and internal impacts for successful organisational evaluation, Attain one of the four modules you need to gain the International Diploma in Business Analysis\n\n', 'Who is it for?', NULL, 'The Foundation certificate is for people who want to gain a deep understanding of the many processes/framework and techniques needed to implement successful business change,\nSuitable for business analysts, business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business change, including:', 'An appreciation of the principles processes roles and approaches involved in business change,\r\nHow to use information technology to enable business change and as a driver for business change,\r\nHow to align the organisation with external and internal influences,\r\nHow to implement business change effectively', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £192(£160 + VAT)', NULL, NULL, 11, '2022-01-24 11:30:17', '2022-01-24 11:30:17', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (15, 'Practitioner Certificate in Business Analysis Practice', NULL, NULL, 'Take your career to the next level by learning how to support successful business change, Explore a range of strategic analysis and performance management techniques, Apply business analysis techniques within a defined framework, Attain one of the four modules you need to gain the International Diploma in Business Analysis', 'Who is it for?', NULL, 'This Practitioner Certificate is designed for people who want to understand a range of business process modelling and analysis techniques know how they are used and identify when to use them,\nSuitable for business analysts business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business change, including:', 'An appreciation of the principles processes roles and approaches involved in business change,\r\nHow to use information technology to enable business change and as a driver for business change,\r\nHow to align the organisation with external and internal influences,\r\nHow to implement business change effectively', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: 2282(£190 + VAT)\r\n\r\nTextbook: Business Analysis 4th edition Please note: Please refer to your syllabus for the required reading relevant to that syllabus and exam.\r\n\r\n', NULL, NULL, 13, '2022-01-24 12:06:17', '2022-01-24 12:06:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (16, 'Practitioner Certificate in Benefits Management and Business Acceptance', NULL, NULL, 'Demonstrate how a rigorous approach to benefits management ensures successful software solutions delivery, Identify the benefit owners and key stakeholders within your environment, Recognise what good business acceptance testing looks like, Attain one of the four modules you need to gain the International Diploma in Business Analysis', 'Who is it for?', NULL, 'This Practitioner Certificate is designed for people who want to understand a range of business process modelling and analysis techniques know how they are used and identify when to use them,\r\nSuitable for business analysts business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business change, including:', 'An appreciation of the principles processes roles and approaches involved in business change,\r\nHow to use information technology to enable business change and as a driver for business change,\r\nHow to align the organisation with external and internal influences,\r\nHow to implement business change effectively', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14, '2022-01-24 12:06:17', '2022-01-24 12:06:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (19, 'Practitioner Certificate in Modelling Business Processes', NULL, NULL, 'Demonstrate you can apply practical business analysis techniques that can help to improve your business processes, Look at ways to investigate and analyse the ways your organisation carries out its operations, Understand and know how to apply different process modelling techniques including both the organisational view and more detailed models, Attain one of the four modules you need to gain the International Diploma in Business Analysis', 'Who is it for?', NULL, 'This Practitioner Certificate is designed for people who want to understand a range of business process modelling and analysis techniques know how they are used and identify when to use them,\r\nSuitable for business analysts business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business change, including:', 'An appreciation of the principles processes roles and approaches involved in business change,\r\nHow to use information technology to enable business change and as a driver for business change,\r\nHow to align the organisation with external and internal influences,\r\nHow to implement business change effectively', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £228(£190 + VAT)', NULL, NULL, 17, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (21, 'Practitioner Certificate in Data Management Essentials', NULL, NULL, 'Demonstrate how to implement good practice in data management, Understand how to develop procedures for managing corporate date, Know how to work within the data management team and the roles and responsibilities within data management, Attain one of the four modules you need to gain the International Diploma in Business Analysis', 'Who is it for?', NULL, 'This Practitioner Certificate is designed for people who want to understand a range of business process modelling and analysis techniques know how they are used and identify when to use them,\r\nSuitable for business analysts business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business change, including:', 'An appreciation of the principles processes roles and approaches involved in business change,\r\nHow to use information technology to enable business change and as a driver for business change,\r\nHow to align the organisation with external and internal influences,\r\nHow to implement business change effectively', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £228(£190 + VAT)', ' What format is the exam?', 'One hour ‘open book’ written exam, preceded by 15 minutes\' reading time,\r\nBased on a business scenario,\r\nPass mark is 50%', 18, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (22, 'Foundation Certificate in Requirements Engineering', NULL, NULL, 'Learn how to take a best practice approach to defining business requirements,\nDevelop a set of practices and processes to identify requirements in order to develop and design business solutions,\nTechniques to engage with stakeholders and draw out information to define requirements,\nAttain one of the four modules you need to gain the International Diploma in Business Analysis', 'Who is it for?', NULL, 'This Practitioner Certificate is designed for people who want to understand a range of business process modelling and analysis techniques know how they are used and identify when to use them,\r\nSuitable for business analysts business managers and members of their team business change managers and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'This certificate was updated (July 2021) as a part of our aim to continuously improve the quality of learning within our professional certifications. This update allows BCS to remain at the forefront of best practice industry standards for professional certifications. In the updated BCS Practitioner Certificate in Requirements Engineering you’ll learn how to take a systematic approach to eliciting, defining, analysing, validating, documenting and managing requirements.\r\nThis certificate includes:', 'The Requirements Engineering Framework,\r\nHow to elicit requirements and techniques to use,\r\nHow to collaborate and communicate with stakeholders in the requirements process,\r\nHow to write a user story,\r\nBest practice approaches to document requirements,\r\nHow to manage requirements,\r\nValidating prioritisig and quality assurance in the requirements process,\r\nAnalysing and evaluation of requirements', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £228(£190 + VAT)\r\nTextbook: Business Analysis 4th edition Please note: Please refer to your syllabus for the required reading relevant to that syllabus and exam.', ' What format is the exam?', 'One hour ‘open book’ written exam, preceded by 15 minutes\' reading time,\r\nBased on a business scenario,\r\nPass mark is 50%', 19, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (23, 'Professional Certificate in Advanced Requirements Engineering', NULL, NULL, 'Gain a deeper understanding of the requirements engineering framework, Define different business roles and problem statements, Showcase your ability to understand enterprise governance, Progress towards the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'The professional certificate is designed for people who want to understand apply and realise business benefit processes in change projects, Suitable for business analysts business architects, project managers programme managers business managers and members of their team', ' Entry Requirements', '', 'There are no entry requirements for this certification,\nWe recommend that candidates have gained the BCS Practitioner Certificate in Requirements Engineering', 'What will I learn?', 'You will extend your knowledge of requirements engineering including:', 'How to apply the Business Model canvas to business scenarios,\r\nDelivering a requirements engineering plan,\r\nHow to evaluate a problem statement and explain the Outcome Frame,\r\nStakeholder engagement skills,\r\nHow to produce non-functional requirements', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', '', '', '', '', '', '', '', '', ' What format is the exam?', '90-minute ‘closed book’ with 40 multiple choice questions,\r\nPass mark is 65% (26/40)', 21, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (24, 'Professional Certificate in Agile Business Analysis', NULL, NULL, 'Gain a deeper understanding of the requirements engineering framework, Define different business roles and problem statements, Showcase your ability to understand enterprise governance, Progress towards the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'This professional certificate is designed for people who want to understand Agile ways of working and how they support the business analysis role,Suitable for business analysts \nbusiness architects business managers and members of their team and project managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business analysis, including:', 'How to describe a range of agile methods (process-roles-principles),\nThe role of business analysis in an agile team,\nThe use of agile analysis-modelling and documentation techniques,\nHow to analyse business goals and their decomposition,\nHow to evaluate whether an agile method is appropriate for a given situation,\nUnderstanding how to apply user story techniques,\nHow to explain the techniques used to plan and manage an iteration', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, ' What format is the exam?', '90 minute \'closed book\' with 40 multiple choice questions. Please note: this exam has been updated to an online format. The new syllabus reflects a change in exam format only syllabus content has not changed.,\nPass mark is 65% (26/40)', 22, '2022-01-24 12:06:17', '2022-01-24 12:06:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (26, 'Professional Certificate in Benefits Planning Realisation', NULL, NULL, 'Demonstrate you can apply the process and techniques used to plan manage and realise business benefits, Employ skills and knowledge to support successful business change programmes within your organisation', 'Who is it for?', NULL, 'The professional certificate is designed for people who want to understand apply and realise business benefit processes in change projects, Suitable for business analysts business architects project managers programme managers business managers and members of their team', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of managing and applying benefits in projects, including how to:', 'Discover the context for benefits management,\r\nDefine and employ a benefits management process,\r\nIllustrate the organisational drivers and business benefits,\r\nBuild a business case and classify business benefits,\r\nUndertake an investment appraisal,\r\nShow the need for stakeholder and change management,\r\nExplain the tasks required in implementing this approach and predict future issues', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £342(£285 + VAT)', ' What format is the exam?', '90-minute ‘closed book’ with 40 multiple choice questions,\nPass mark is 65%', 23, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (27, 'Professional Certificate in Business Architecture', 'Apply frameworks philosophy, principles and techniques of business architecture, and be able to explain the importance of business architecture in a change context.      \nDescribe the use of business architecture in assessing the impact of change and ensuring the effectiveness of business change', 'Develop skills and knowledge to support successful business change programmes within your organisation.\nAttain one of the four modules you need to gain the Advanced International Diploma in Business Analysis', 'Apply frameworks philosophy, principles and techniques of business architecture and be able to explain the importance of business architecture in a change context, Describe the use of business architecture in assessing the impact of change and ensuring the effectiveness of business change, Develop skills and knowledge to support successful business change programmes within your organisation, Attain one of the four modules you need to gain the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'The professional certificate is for anyone who needs to understand the principles rationale and techniques of the business architecture discipline,\nSuitable for business analysts-business architects-business change managers project managers and business managers', ' Entry Requirements', NULL, 'There are no entry requirements for this certification', 'What will I learn?', 'You’ll gain a broad understanding of business architecture including:', 'The business architecture domain,\nBusiness architecture frameworks,\nBusiness motivation view,\nCapability / organisation view,\nValue / process view,\nCompetency / people view,\nInformation and technology views,\nBusiness architecture and business change', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'What format is the exam?', 'Pass mark is 65% (26/40)\n,90 minute \'closed book\' with 40 multiple choice questions. Please note: this exam has been updated to an online format. The new syllabus reflects a change in exam format only, syllabus content has not changed.', 24, '2022-01-24 12:06:17', '2022-01-24 12:06:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (28, 'Professional Certificate in Business Finance', NULL, NULL, 'Demonstrate your financial management and analysis skills with this internationally recognised certification, A certification that is specifically focused on finance and reporting within business change projects, Develop skills and knowledge to support successful business change programmes within your organisation, Attain one of the four modules you need to gain the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'The professional certificate is designed for people who want to understand apply and realise business benefit processes in change projects, Suitable for business analysts business architects project managers programme managers business managers and members of their team', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of managing and applying benefits in projects, including how to:', 'Finance in the project lifecycle,\r\nDeveloping a business case,\r\nConstructing a financial case,\r\nBudgets and budgeting,\r\nCosts and cost behaviour,\r\nEvaluating supplier financial stability,\r\nFinancing service deliver', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £342(£285 + VAT)', ' What format is the exam?', '90-minute ‘closed book’ multiple choice questions,\r\nPass mark is 65%\r\n', 25, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (29, 'Professional Certificate in Data Analysis', NULL, NULL, 'Define data requirements demonstrating a detailed understanding and rigour, Illustrate the benefits and uses of data analysis and the techniques applied when analysing business data, Develop skills and knowledge to support successful business change programmes within your organisation, Attain one of the four modules you need to gain the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'The professional certificate is designed for people who want to understand apply and realise business benefit processes in change projects, Suitable for business analysts business architects, project managers programme managers business managers and members of their team', ' Entry Requirements', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of managing and applying benefits in projects, including how to:', 'Concepts and principles of data analysis and modelling,\r\nEntity relationship modelling,\r\nHow to rationalise data,\r\nAnalyse class modelling,\r\nValidation techniques', NULL, NULL, NULL, 'How do I get this certification?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last three days.\r\n\r\n', 'Where can I study?', 'At an approved provider near you and\r\nat your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about Business Analysis.', 'How long will it take?', 'It usually takes a minimum of 27 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £342(£285 + VAT)', ' What format is the exam?', '90-minute ‘closed book’ with 40 scenario-based multiple choice questions,\nPass mark is 65%\n', 26, '2022-01-24 12:36:17', '2022-01-24 12:36:48', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (30, 'BCS Foundation Level Certificate in DevOps', 'Develop your understanding of DevOps methodology, tools and practices, and how to deliver applications and services more effectively.', NULL, NULL, 'Who is it for?', NULL, 'People involved in or implementing the DevOps process including roles such as DevOps evangelist automation architect; cloud infrastructure engineer software developer software tester security engineer database administrator and product owners. Read the syllabus below for a more complete list of roles.', 'Entry requirements', NULL, 'None. Training with a BCS accredited training provider is highly recommended.', 'What will I learn?', 'Candidates should be able to demonstrate knowledge, understanding and some basic application of the following aspects of DevOps:', 'Origins,\nBenefits,\nCulture and teams,\nAutomation,\nLean,\nMeasurement,\nSharing,\nCommon roles,\nPractices and techniques,\nMethods and approaches for teams', NULL, NULL, NULL, 'How do I get this certification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last two days.', 'Where can I study?', NULL, 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '\nWhat format is the exam?', 'One hour \'closed book\' with 40 multiple choice questions,Pass mark is 65% (26/40)', 27, '2022-01-26 10:52:35', '2022-01-26 10:52:35', '/storage/bt.png');
INSERT INTO `page_certificate_details` VALUES (31, 'Professional Certificate in Stakeholder Engagement', NULL, NULL, 'Gain a deeper understanding of the requirements engineering framework, Define different business roles and problem statements, Showcase your ability to understand enterprise governance, Progress towards the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'The professional certificate is for anyone who is responsible for engaging in and managing stakeholder relationships in a business change environment', 'What are the entry requirements?', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of stakeholder engagement, including:', 'Stakeholder communication,\nWorking with stakeholder groups,\nFacilitated workshop techniques,\nHow to build rapport,\nCreative problem-solving,\nManaging expectations,\nNegotiating with stakeholders,\nManaging conflict', NULL, NULL, NULL, 'How do I get this certiﬁcation?', 'Take the complete training programme (recommended)', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.', 'How long will it take?', 'Classroom courses normally last two days.', 'Where can I study?', 'At an approved provider near you and\nAt your computer via e-learning or web conference learning', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', 'Exam only (study on your own)', 'Ideal if you’re self-motivated and already know something about business analysis.', 'How long will it take?', 'It usually takes a minimum of 16 hours to prepare for the exam.', 'Where can I take the exam?', 'At any Pearson VUE test centre across the world', 'How much does it cost?', 'Exam price: £342 (£285 + VAT)', '\nWhat format is the exam?', '90 minute ‘closed book’ with 40 multiple choice questions,\nPass mark is 65%', 28, '2022-01-26 11:50:49', '2022-01-26 11:50:49', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (32, 'BCS Foundation Certificate in Data Protection', 'Explore the application of current data protection laws, including the EU General Data Protection Regulation (GDPR), UK GDPR and the UK Data Protection Act 2018.', NULL, NULL, 'Who is it for?', NULL, 'Anyone who needs to understand data protection and GDPR in particular.', 'Entry requirements', NULL, 'None. Some knowledge of data protection law would be an advantage to the candidate as well as basic working knowledge of IT and GDPR.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'Hold a recognised qualification in data protection.,\nGain an understanding of the key changes that the GDPR and the Data Protection Act (2018),\nGain an understanding of the new rights available to data subjects,\nThe implications of those rights with the GDPR and Data Protection Act (2018).,\nGain an understanding of responsibilities under the GDPR and the Data Protection Act (2018), \nGain an understanding of the increased obligations faced by data controllers and data processors,\nBe better placed to support their organisation in processing customer data in compliance with the GDPR and the Data Protection Act (2018).\n', NULL, NULL, NULL, 'How do I get this certification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and have a good understanding of data protection.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where do I sit my exam?', 'At any Pearson Vue test centre across the world.', 'How much does it cost?', 'Exam price: £192 (£160 + VAT).', 'What format is the exam?', 'One hour ‘closed book’ examination with 40 multiple choice questions,\nPass mark is 26/40 (65%)', 29, '2022-01-27 09:17:53', '2022-01-27 09:17:53', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (33, 'BCS Practitioner Certificate in Data Protection', 'For experienced Data Protection professionals, and those candidates that want to build on the BCS Foundation Certificate, you can gain a broader and deeper understanding of current laws, including the EU GDPR, UK GDPR and the UK Data Protection Act 2018, and how they need to be applied in your organisation.', NULL, NULL, 'Who is it for?', NULL, 'Anyone who has some existing responsibility for data protection within their organisation.,\nIt’s also useful for those who want to broaden their basic understanding in this area and fully understand the practical applications of data protection laws.,\nWhilst this certificate is written to the UK Data Protection Act, many other jurisdictions have enacted broadly similar data protection laws, so international candidates may also find this useful.', 'Entry requirements', NULL, 'It\'s ideal if you hold the BCS Foundation Certificate in Data Protection though this is not mandatory.,We strongly recommended that candidates attend an accredited training course and are fully familiar with GDPR and the syllabus and any recommended reading outlined in the syllabus.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'Key changes that the GDPR and the UK Data Protection Act introduce to data protection,\nResponsibilities under the GDPR and the UK Data Protection Act,\nApply the new rights available to data subjects,\nUnderstand the designation position and role / tasks of a data protection officer,\nHandle personal data in compliance with the GDPR and the UK Data Protection Act.\n\n', NULL, NULL, NULL, 'How do I get this certification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses usually last between three and five days dependent on training provider.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about data protection.', 'How long will it take?', 'It usually takes between 20 to 40 hours dependent on experience.', 'Where do I sit my exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Guide price: £228 (£190 + VAT)', 'What format is the exam?', 'A 90 minute closed book digital 40 question multi-choice exam,\nPass mark is 65%', 30, '2022-01-27 09:22:52', '2022-01-27 09:22:52', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (34, 'Artificial Intelligence Foundation Pathway', 'This bite-size learning pathway is a practical and convenient way to build your knowledge of AI, enabling you to work confidently, competently and responsibly in and alongside AI development.', NULL, NULL, 'About artificial intelligence (AI)', 'AI is a powerful tool that increasingly supports our way of life, at home and in the workplace. From predicting the weather and monitoring our heating, to collating election results, scanning faces on CCTV and analysing vast amounts of customer data, AI is applied by both businesses and consumers to manage complex activities with speed, efficiency and precision.  In such a fast-moving and complex field, the challenge is in understanding the evolving uses of AI and the far-reaching ethical implications. The reality today is that AI is being implemented by teams and organisations who don’t yet comprehend its potential. By the same token, consumers are adopting AI technology without knowing the true impact it could be having on their lives.', NULL, 'What are the benefits of the AI Foundation Pathway?', 'The BCS AI Foundation Pathway enables you to develop a solid understanding of this cutting-edge technology through efficient bite-size learning. The unique approach lets you select the learning that’s relevant to your own career or user journey, as and when you need it, and without having to sit a full certification.  You may be starting out in your AI career looking to build core knowledge, or you work in one of the many business areas impacted by AI - business analysis, software testing, agile, user experience to name just a few - and you need to grasp a better understanding. Maybe you’re a tech consumer simply interested in this exciting and innovative field. Whichever your perspective, the AI Foundation Pathway offers the ideal route for your AI education.', 'Bite-size learning is proven as an efficient and highly effective way to develop your knowledge and skills,The AI pathway takes a holistic approach encompassing the different ways of working and thinking in the field,You can develop knowledge in your chosen area of AI without having to invest the time and money you’d need to sit a full certification,It’s a practical alternative to university study and a fantastic opportunity to kickstart your AI career', 'How does the pathway work?', 'Choose from 12 awards set within four key areas of AI:', 'business innovation,data,ethics,machine learning and other AI techniques\n            ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 31, '2022-01-27 09:33:25', '2022-01-27 09:33:25', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (35, 'Professional Certificate in Advanced Requirements Engineering', NULL, NULL, NULL, 'Who is it for?', NULL, 'This professional certificate is designed for people who want to understand Agile ways of working and how they support the business analysis role,Suitable for business analysts business architects business managers and members of their team and project managers', 'What are the entry requirements?', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of business analysis, including:', 'How to describe a range of agile methods (process-roles-principles)\nThe role of business analysis in an agile team,\nThe use of agile analysis-modelling and documentation techniques,\nHow to analyse business goals and their decomposition,\nHow to evaluate whether an agile method is appropriate for a given situation,\nUnderstanding how to apply user story techniques,\nHow to explain the techniques used to plan and manage an iteration', NULL, NULL, NULL, NULL, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 21, '2022-01-27 09:41:29', '2022-01-27 09:41:29', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (39, 'Professional Certificate in Team Leadership', NULL, NULL, 'Demonstrate your leadership ability with our internationally-recognised team leadership certification, Apply leadership frameworks and techniques in your senior role or prepare to take the next step into people management, Develop skills and knowledge to support successful business change programmes within your organisation, Attain one of the four modules you need to gain the Advanced International Diploma in Business Analysis', 'Who is it for?', NULL, 'The professional certificate is for anyone who is wants to further their understanding of leadership frameworks and techniques,Suitable for business analysts project managers product managers and business managers', 'What are the entry requirements?', NULL, 'There are no entry requirements for this certification.', 'What will I learn?', 'You’ll gain a broad understanding of team leadership, including:', 'Team leadership roles and styles,\nEmotional intelligence,\nParticipative leadership,\nPositive communication,\nManaging team performance,\nManaging time and priorities,\nLeading teams through change', NULL, NULL, NULL, 'How do I get this certification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last two days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Exam only (self study)', 'How long will it take?', 'It usually takes a minimum of 16 hours to prepare for the exam.', 'Where do I sit my exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £342 (£285 + VAT)', 'What format is the exam?', '90 minute ‘closed book’ with 40 multiple choice questions,\nPass mark is 65%', 33, '2022-01-27 11:53:06', '2022-01-27 11:53:06', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (40, 'BCS Foundation Certificate in User Experience', 'Understand the principles behind optimal design and architecture – and start improving user experience for your customers.', NULL, NULL, 'Who is it for?', 'Anyone involved in the design and evaluation of user interfaces (interface designers, usability engineers, requirement engineers, product managers).', NULL, 'Entry requirements', NULL, 'None. Training with a BCS accredited training provider is recommended.', 'What will I learn?', NULL, 'Guiding principles, User research, Illustrating the context of use, Measuring usability, Information architecture, Interaction design, Visual design, User interface prototyping, Usability evaluation,', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about user experience.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where do I sit my exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £192 (£160.00 + VAT)', 'What format is the exam?', 'One hour ‘closed book’ with 40 multiple choice questions,\nPass mark is 65% (26/40)', 34, '2022-01-27 12:04:31', '2022-01-27 12:04:31', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (41, 'BCS Foundation Certificate in IS Project Management', 'Understand the principles of project management and techniques to support industry good practice complementary to PRINCE2®.', NULL, NULL, 'Who is it for?', NULL, 'This certificate is appropriate for anyone involved in or affected by IT projects; this extends to users buyers and directors.', 'Entry requirements', NULL, 'No entry requirements although the candidate is expected to have basic working knowledge of IT.', 'What will I learn?', 'Candidates will be able to demonstrate and execute an understanding of the principles of project management in their organisation, including an understanding of:', 'Project planning,\nMonitoring and control,\nChange control and configuration management,\nEffort estimation,\nQuality and risk management,\nCommunication between project, stakeholders', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'How much does it cost?', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about user experience.', 'How long will it take?', 'It usually takes a minimum of 18 hours to prepare for the exam.', 'Where do I sit my exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £192 (£160.00 + VAT)', 'What format is the exam?', 'One hour ‘closed book’ with 40 multiple choice questions,\nPass mark is 65% (26/40)', 35, '2022-01-27 12:09:13', '2022-01-27 12:09:13', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (42, 'BCS EXIN Essentials Certificate in Blockchain', 'Take your first steps in blockchain training to understand the basics of blockchain, as well its challenges, applications and innovations.', NULL, NULL, 'Who is it for?', 'Anyone who wants to understand more about blockchain and the impact it can have on their organisation and society.', NULL, 'Entry requirements', NULL, 'There are no formal requirements for this certification.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'Explain how a blockchain works,\nExplain the purpose ledgers have in a blockchain,\nIdentify blockchain vulnerabilities,\nExplain the role of the public witness in a blockchain,\nExplain the role of decentralized autonomous organisations (DOA) and sophisticated smart contracts,\nDescribe possible applications for a blockchain with regard to identity.,\nIdentify the possibilities of combining a blockchain with internet of things (IoT) or artificial intelligence (AI),\nDescribe the role a blockchain can play in the supply chain,\nExplain what digital fiat currency and disruption in banking and currency are,\nExplain the use of blockchain technology for the protection of intellectual property right (IP) and providence', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last for a minimum of one day.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What format is the exam?', '30 minute ‘closed book’ with 20 multiple choice questions,\nPass mark is 65% (13/20)', 36, '2022-01-31 11:39:35', '2022-01-31 11:39:35', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (43, 'BCS Foundation Certificate in Information Security Management Principles', 'Gain a clear understanding of IS management issues including risk management, security standards, legislation and business continuity.', NULL, NULL, 'Who is it for?', 'Anyone with an interest in information security, whether as a career or for general business knowledge.', NULL, 'Entry requirements', NULL, 'There are no formal entry requirements however the candidate should have basic working IT knowledge and an awareness of the issues involved with the security control activities., It\'s recommended that candidates read the BCS course approved reference book Information Security Management Principles as well as attend training with a BCS accredited training course.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'Knowledge of the concepts relating to information security,\nUnderstanding of current national legislation and regulations which impact upon information security, \nAwareness of current national and international standards, Frameworks and organisations which facilitate the management of information security., \nUnderstanding of the current business and common technical environment, \nKnowledge of the categorisation operation and effectiveness of controls of different types and characteristics. ', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about information security.', 'How long will it take?', 'It usually takes a minimum of 40 hours to prepare for the exam.', 'Where do I sit my exam?', 'At any Pearson VUE test centre across the world.', 'How much does it cost?', 'Exam price: £192 (£160.00 + VAT)', 'What format is the exam?', 'Two hour ‘closed book’ with 100 multiple choice questions,\nPass mark is 65% (65/100)', 37, '2022-01-31 11:55:40', '2022-01-31 11:55:40', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (44, 'BCS EXIN Foundation Certificate in Blockchain', 'Gain a clear understanding of the basic concepts of blockchain and the potential fields of application, as well as the potential value for the organisation and the technology driving blockchain.', NULL, NULL, 'Who is it for?', 'Anyone who wants to understand blockchain and its wider application in their own organisation.', NULL, 'Entry requirements', '', 'There are no formal requirements but it is advisable to complete the BCS EXIN Essentials Certificate in Blockchain.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'Explain how a blockchain works,\nIdentify the role of a node in a network,\nDifferentiate between public private and hybrid blockchains,\nExplain how cryptography private and public keys and hashes are used in a blockchain,\nIdentify advantages and disadvantages of different consensus algorithms,\nDescribe possible applications for a blockchain with regard to identity.,\nDescribe the role a blockchain can play in the supply chain.,\nDifferentiate between blockchain networks,\nIdentify the possibilities of combining a blockchain with internet of things (IoT) or artificial intelligence (AI),\nExplain the use of blockchain technology for the protection of intellectual property rights (IP) and providence.', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last for a minimum of two days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What format is the exam?', 'One hour ‘closed book’ with 40 multiple choice questions,\nPass mark is 65% (26/40)', 38, '2022-01-31 12:13:50', '2022-01-31 12:13:50', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (45, 'BCS Practitioner Certificate in Business Continuity Management', 'Learn how to significantly contribute to your organisation\'s business continuity management process in this ‘hands-on’, practical course.', NULL, NULL, 'Who is it for?', 'Anyone who is involved in the areas of information security and information assurance.', NULL, 'Entry requirements', NULL, 'There are no formal entry requirements however the candidate will require an understanding of information assurance., It\'s recommended that candidates attend a BCS accredited training course.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'The function of and need for business continuity management within an organisation,\nThe business continuity management life cycle,\nThe main components of a business continuity management programme,\nThe need for risk assessment within business continuity management,\nThe options for development of a business continuity management strategy,\nHow to prepare and develop plans for business continuity management response, \nThe need for exercising maintenance and review of plans,\nThe need for embedding business continuity management awareness within the organisation ', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about business continuity management.', 'How long will it take?', 'It usually takes a minimum of 34 hours to prepare for the exam.', 'How do I sit the exam?', 'If you\'re studying for this qualification without a training provider you can sit this exam directly with BCS.', 'How much does it cost?', 'Exam price: £222 (£185.00 + VAT)', 'What format is the exam?', '\n\nPart A: Ten multiple choice questions (1 mark each),\nPart B: Six compulsory short narrative questions (5 marks each),\nPart C: Three compulsory essay style questions (20 marks each),\nPass mark is 65% (65/100)', 39, '2022-01-31 13:28:58', '2022-01-31 13:28:58', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (46, 'BCS Practitioner Certificate in Freedom of Information', 'Understand how to instill robust information practices, based on the Freedom of Information Act 2000, and the implications for your organisation.', NULL, NULL, 'Who is it for?', 'Anyone managing or responding to requests for information from the public or working in this subject area, for instance information managers or Freedom of Information (FOI) practitioners.', NULL, 'Entry requirements', NULL, 'There are no formal entry requirements however it is strongly recommended that candidates attend a BCS accredited training course. Some knowledge of the FOI law and applying this in a work context would be beneficial.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'How specific exemptions might apply to requests for information including the application of the public,\nA clear and balanced approach to applying legal judgements in accordance with the law and case law.,\nImplementation of internal management procedures to support freedom of information legislation.,  \nKnowledge of the definition of the Act and how it interacts with legislation, Specifically the Environmental Information Regulations 2004 and the Data Protection Act 2018.,\nDevelopment of internal procedures to support your authority’s compliance with the FOI Act,\nHow to apply the legislation in a practical setting relevant to your work.,\nHow to prepare clear advice to colleagues as well as the public.', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'We recommend 40 hours of training with a provider.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about freedom of information.', 'How do I sit the exam?', 'If you\'re studying for this qualification without a training provider you can sit this exam directly with BCS.', 'How much does it cost?', 'Exam price: £222 (£185.00 + VAT)', NULL, NULL, 'What format is the exam?', '90 minute scenario based ‘closed book’ digital exam,\n6 multiple choice questions (State and Describe),\n12 multiple response questions (Explain),\n3 scenario-based questions (Apply and Analyse),\nPass mark of 65%', 40, '2022-01-31 13:51:42', '2022-01-31 13:51:42', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (47, 'BCS Practitioner Certificate in Information Assurance Architecture', 'In line with your CCP training, learn more about building an architecture that meets business requirements while mitigating risk.', NULL, NULL, 'Who is it for?', 'Security professionals looking to understand the technical and business aspects of the profession or anyone looking to work in the field of security architecture.', NULL, 'Entry requirements', NULL, 'There are no formal entry requirements however candidates will require an understanding of information assurance. ,It\'s recommended that candidates attend a BCS accredited training course.', 'What will I learn?', 'Candidates should be able to complete the following beforehand:', 'Describe the business environment and the information risks that apply to the systems.,\nDescribe and apply security design principles.,\nIdentify information risks that arise from potential solution architectures.,\nDesign alternate architectures or countermeasures to mitigate risks.,\nEnsure that proposed architectures and countermeasures adequately mitigate identified information risks.,\nApply ‘standard’ security techniques and architectures to mitigate security risks.,\nDevelop new architectures that mitigate the risks posed by new technologies and business practices.,\nProvide consultancy and advice to explain Information Assurance and architectural problems.,\nSecurity configure ICT systems in compliance with their approved security architectures.', NULL, NULL, NULL, 'How do I get this qualification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last five days.', 'Where can I study?', 'At a BCS accredited training provider near you.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (self study)', 'Ideal if you’re self-motivated and already know something about information assurance.', 'How do I sit the exam?', 'If you\'re studying for this qualification without a training provider you can sit this exam directly with BCS.', 'How much does it cost?', 'Exam price: £264 (£220.00 + VAT)', NULL, NULL, 'What format is the exam?', 'Two hour ‘closed book’ scenario based examination,\n\nPart A: 60 multiple choice questions (1 mark each),\nPart B: 25 scenario based multiple choice questions (65 marks available),\nPass mark is 81/125 (65%).', 41, '2022-02-01 09:43:31', '2022-02-01 09:43:31', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (48, 'NCSC Certified Cyber Professional (CCP) assured service', 'The National Cyber Security Centre (NCSC) recognition sets the professional standards for cyber practitioners in the UK, supporting the development of a robust and respected cyber security profession.', NULL, NULL, 'What is the Certified Cyber Professional (CCP) assured service?', 'The CCP assured service has been developed by the NCSC in consultation with government, industry and academia to address the growing need for specialists in the cyber security profession, and sets the standard for UK cyber security professionals.', NULL, 'Changes to the CCP assured service', 'Following a pilot, the CCP assured service is changing. When CCP was originally introduced, people were certified in cyber security roles. After listening to feedback from users and professionals, NCSC has decided to refresh and streamline the assessment process to focus more closely on critical skills. As a result, candidates will now be recognised in specialisms instead of roles.', 'Please note: NCSC will honour the expiry date on role certifications already awarded. No-one will be required to hold both a role and a specialism certification for the same cyber security activity. Revalidations (involving the submission of CPD evidence) at the 18-month point for existing CCP role certifications will continue as normal until September 2022.,\n\nIf you have any queries please contact ccpsupport@bcs.uk.', 'Submit an application to have your Risk Management experience recognised', 'If successful in the assessment process to determine your CCP level (which includes a comprehensive interview), you will be able to:', 'Take your place on the public CCP register and gain wider recognition for your competence and experience,\n\nValidate your cyber security credentials and your role in protecting your organisation’s assets,\n\nTake up a year’s free BCS membership when you gain this recognition (offer available to non-members only),\n\nJoin a community of practitioners raising standards and building the reputation of the UK’s cyber profession', 'What’s involved in the application process?', 'To apply for CCP, you will need to ensure you have one of the following foundational pre-requisites:', 'An NCSC-certified degree (undergraduate or postgraduate) or,\nA valid certificate for Certified Information Systems Security Professional (CISSP) including full membership of (ISC)² or,\nA valid certificate for Certified Information Security Manager (CISM)  including full membership of ISACA or,\nFull membership of the Chartered Institute of Information Security (CIISec) or,\nProof of having passed an appropriate NCSC internal skills level assessment or,\nProof of having completed an internal NCSC professional development framework (for example for cyber security architecture).', 'How do the existing and new CCP assured service compare?', 'Previous service : Roles based', 'Three levels: 6 skills based roles', 'Lead Practitioner', NULL, 'Senior Lead Practitioner', NULL, 'Practitioner', NULL, 'New service', 'Specialism based: Two level based', 'Certified Cyber Professional', '', 'Associate Cyber Professional', NULL, NULL, '1 specialism to launch first - Risk Management - with others to follow in due course.', 'How much does it cost?', 'The application fee is £2550.00 +VAT.', 42, '2022-02-01 10:05:35', '2022-02-01 10:05:35', '/storage/1.png');
INSERT INTO `page_certificate_details` VALUES (49, 'Advanced International Diploma in Business analysis', 'Gain independent, industry-wide recognition as a senior business analyst by demonstrating your experience, expertise and contribution to the BA profession.', NULL, 'Validate your knowledge skills and capabilities and gain entry onto the the professional registration for IT technicians (RITTech) following successful completion of your International Diploma, Attain an internationally-recognised certification, Choose to work towards the Diploma module by module or take all the modules together in a bundled training programme, Exemptions for holders of IIBA IREB or Organisational Context certifications. Find out more about exemptions', 'Who is it for?', 'Experienced senior business analysts who actively contribute to the development and promotion of the business analysis profession.', NULL, 'Entry Requirements', 'There are no formal entry requirements however candidates will require an understanding of information assurance. It\'s recommended that candidates attend a BCS accredited training course.', 'BCS International Diploma in Business Analysis,\r\nBCS Professional level BA certificates in each of the analytical, business and people skills specialisms,\r\nAdditional BCS Professional level BA certificate in either the analytical or business skills specialism,\r\nAt least five years’ BA experience\r\n', '', '', NULL, '', '', '', '', 'How do I get the BA Advanced International Diploma?', 'In addition to holding prerequisite certification, to achieve the Advanced International Diploma in Business Analysis you’ll need to demonstrate your experience, CPD, and contribution to the BA community, in a written portfolio.', '', '', '', NULL, NULL, NULL, '', '', '', '', '', NULL, NULL, NULL, 'How much does it cost?', 'The application fee is £714 (£595 +VAT)', 43, '2022-02-01 10:05:35', '2022-02-01 10:05:35', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (50, 'Expert BA Award', 'Validate your high-level business analysis skills and expertise by achieving the Expert BA Award, endorsed by BCS in partnership with the BA Manager Forum (BAMF) and Chartered Management Institute (CMI).', NULL, NULL, 'Who is it for?', NULL, 'Senior business analysts who conduct complex BA activities engage with senior stakeholders and can demonstrate their impact to the organisation at a strategic level.', 'What are the entry requirements?', NULL, 'You’ll need prerequisite certification and proven core and specialist BA skills.', 'What will I learn?', 'The application process for the award comprises the following steps:', 'prerequisite certification and experience,\r\nwritten submission of evidence demonstrating your core and specialist skills and contribution to the BA profession,\r\nsupporter validation,\r\nproject assignment,\r\ninterview with assessors', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 44, NULL, NULL, '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (53, 'International Diploma in Business Analysis', NULL, NULL, 'Validate the knowledge skills and capabilities you have gained so far,\nAttain an internationally-recognised certification,\nChoose to work towards the Diploma module by module or take all the modules together in a bundled training programme,\nExemptions for holders of IIBA IREB or Organisational Context certifications. Find out more about exemptions', 'Who is it for?', NULL, 'The International Diploma is designed for business and IT professionals who want to demonstrate they have a detailed understanding of business analysis best practice', ' How do I get the diploma?', NULL, 'You need to pass 4 modules, Then sit the oral examination', NULL, NULL, NULL, NULL, NULL, NULL, 'How do I get this certification?\r\n', 'Book your oral exam', 'Once you have completed your 4 chosen modules you can book the exam with us', 'When can I take the exam?', 'All exams take place online and instructions will be sent to you after booking.', 'How much does it cost?', 'Exam price: £390 (£325 + VAT)', NULL, NULL, 'Take the complete training programme', 'You can take a bundled programme of courses with one of our approved training providers. You’ll find them across the UK and around the world.\n\n', 'You can choose when you take each of the 4 modules', 'Each module typically takes 3 days of classroom study followed by an exam', 'How much does it cost?', 'Prices for courses vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, 45, NULL, NULL, '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (54, 'BCS Practitioner Certificate in Systems Modelling Techniques', 'Develop valuable practical skills by constructing three types of model and demonstrating the interactions between them from varying perspectives.', NULL, NULL, 'Who is it for?', NULL, 'Anyone who is involved in any aspect of systems modelling.', 'Entry Requirements', NULL, 'None.', 'What will I learn?', NULL, 'Justify the need for IT system modelling and modelling techniques, Explain why it is important to model IT system requirements from different perspectives, Develop models of system functionality. These models should be either process models with supporting process descriptions or use case diagrams with supporting use case descriptions. , Develop models of system data. These models should be either entity relationship models or analysis class models both with supporting descriptions., Develop a dynamic model. This model should be either an entity life history showing the effect of events on an entity or a sequence diagram showing the realisation of a use case., Evaluate selected models against business objectives and system requirements., Appreciate how the selected models inter-relate with each other, Describe how the products of analysis feed into the design and development of a system.', NULL, NULL, NULL, 'How do I get this certification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.\r\n\r\n', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What format is the exam?', 'One hour \'open book\' with 15 minutes reading time,Written based on a business scenario,Pass mark is 50%', 46, '2022-01-21 12:04:47', '2022-01-21 12:04:47', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (55, 'BCS Practitioner Certificate in Systems Development Essentials', 'Explore generic lifecycle types, methods and approaches and the systems investigation and quality assurance that underpins the development.', NULL, NULL, 'Who is it for?', NULL, 'Anyone who is involved in any aspect of systems development.', 'Entry Requirements', NULL, 'None.', 'What will I learn?', NULL, 'Identify the tasks and disciplines required for systems development and the implementation of the development, Describe the relationship between systems development and the wider term solution development, Interpret the business requirements and produce systems requirements, Describe the commonly used development lifecycles defined in the syllabus, Select a particular development lifecycle based on specific characteristics, Describe in detail one method that embraces one (or more) of these generic lifecycles, Describe the structure, activities and deliverables of this method, Identify the key roles and responsibilities within the chosen method and describe how these can be used to form teams, Describe interpret and quality assure the key models that the selected method uses for defining the process static and event processes of the system, Explain the differences between logical and physical models, Make effective use of different methods of interpersonal communications, Quality assure the systems requirements documentation and models, Identify different architectures for systems development solutions, Conduct a quality review, Explain how CASE CAST and Application Management tools might be used to support the chosen method', NULL, NULL, NULL, 'How do I get this certification?', 'Training and exam (recommended)', 'Take this course with one of our accredited training providers.', 'How long will it take?', 'Classroom courses normally last three days.', 'Where can I study?', 'At a BCS accredited training provider near you.\r\n\r\n', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What format is the exam?', 'One hour \'open book\' with 15 minutes reading time,Written based on a business scenario,Pass mark is 50%', 47, '2022-01-21 12:04:47', '2022-01-21 12:04:47', '/storage/btb.png');
INSERT INTO `page_certificate_details` VALUES (56, 'BCS Certificate in IT', 'Take the first step in your HEQ journey - the Certificate level focuses on three core modules and is comparable to the first year of a university degree.', NULL, NULL, 'Who is it for?', NULL, 'The Certificate in IT is for students looking to build a solid foundation for a career in IT and computing', 'What are the entry requirements?', '', 'You need to be a BCS member to sit the exams\r\n', 'What will I learn?', 'Three core modules covering fundamental principles in IT:', NULL, '\r\nIs there any exemption?', '', 'If you already hold a qualification that covers the HEQ syllabus you may qualify for exemption from a particular module.', 'How do I get this qualification?', 'Complete training programme (recommended)', 'Complete your study with a BCS approved training provider - they\'re located around the world.', 'How long will it take?', 'Study typically takes six months.', 'Where can I study?', 'We have BCS approved training providers in\r\nGhana,\r\nMauritius,\r\nNepal,\r\nNigeria,\r\nPakistan,\r\nSri Lanka,\r\nZimbabwe', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'Exam only (study on your own)', 'Ideal if you’re self-motivated and already have some basic IT knowledge.', 'How long will it take?', 'To prepare for your exams, we recommend a minimum of 200 hours of study for each of the three modules - that\'s 600 hours in total. There\'s a two-hour exam at the end of each module.', 'Where and When can I sit my exams?', 'Find out more about the annual availability for HEQ exams. We have exam venues across the world, including Colombo and Reduit.', 'How much does it cost?', 'Exam price (per module): £45', 'What format is the exam?', 'Two hour ‘closed book’,\r\nPass mark: 40%', 48, '2022-01-21 12:04:47', '2022-01-21 12:04:47', '/storage/bth.png');
INSERT INTO `page_certificate_details` VALUES (57, 'BCS Diploma in IT', 'Select the modules that match your chosen career path when you study the Diploma in IT – comparable to the second year of a university degree course.', NULL, '', 'Who is it for?', NULL, 'The Diploma in IT is for students who understand the fundamental principles in IT and are ready to start developing specialist skills.', 'What are the entry requirements?', '', 'You need to have passed, or been made exempt from the BCS Certificate in IT.,\r\nYou must be a BCS member to sit the exam.', 'What is the professional IT project?', 'If you’re not going on to study the Professional Graduate Diploma in IT you’ll need to complete a 5000-word professional IT project as part of your Diploma in IT.', 'You’ll need to complete a 5000-word professional IT project as part of your Diploma in IT.', 'Is there any exemption?', '', 'If you already hold a qualification that covers the HEQ syllabus you can apply for exemption from specific modules (no more than two).', 'How do I get this qualification?', 'Complete training programme (recommended)  ', 'Complete your study with a BCS approved training provider - they\'re located around the world', 'How long will it take?', 'We recommend a minimum of 225 hours of study for each module.\r\n\r\nAn additional 200 hours of study will be required if you need to complete a professional project in IT.', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'How long will it take?', 'To prepare for your exam, we recommend a minimum of 225 hours of study for each module. You’ll sit a two-hour exam which covers all modules.', 'Exam and project only (study on your own)', 'Ideal if you’re self-motivated and already an understanding of the subjects of your chosen modules.', 'Where do I submit my project?', 'There are two assessment periods each year: March and September.', 'Where and When can I sit my exams?', 'Find out more about the annual availability for HEQ exams. We have exam venues across the world, including Colombo and Reduit. ', 'How much does it cost?', 'Exam price: £50\r\n\r\nProject submission: £75', 'What format is the exam?', 'Two hour \'closed book\' per module, Pass mark: 40% per module\r\n', 49, '2022-01-21 12:04:47', '2022-01-21 12:04:47', '/storage/bth.png');
INSERT INTO `page_certificate_details` VALUES (58, 'BCS Professional Graduate Diploma in IT', 'Prepare for a rewarding career in technology by developing specialist degree-level knowledge and practical skills aligned with your chosen field of IT.', NULL, '', 'Who is it for?', NULL, 'The Professional Graduate Diploma in IT is for students who are dedicated to working in the IT industry and are looking to gain depth of knowledge and expertise', 'What are the entry requirements?', '', 'You need to have passed or been made exempt from the BCS Certificate in IT and the BCS Diploma in IT., \r\nYou must be a BCS member to sit the exam.', 'What is the professional IT project?', NULL, 'You’ll need to produce a 10000-word professional IT project to complete your Professional Graduate Diploma in IT.', 'Is there any exemption?', '', 'If you already hold a qualification that covers the HEQ syllabus you can apply for exemption from specific modules (no more than two).', 'How do I get this qualification?', 'Complete training programme (recommended)', 'Complete your study with a BCS approved training provider - they\'re located around the world.', 'How long will it take?', 'We recommend a minimum of 250 hours of study for each module, and 300 hours of study to complete your professional project in IT', 'How much does it cost?', 'Course prices vary depending on your chosen training provider and course format.', 'How long will it take?', 'To prepare for your exam, we recommend a minimum of 250 hours of study for each module. You’ll sit a three-hour exam which covers all four of your chosen modules. Practical experience is required to answer some parts of the exam.', 'Exam and project only (study on your own)', 'Ideal if you’re self-motivated and already detailed understanding in the relevant areas of IT.', 'Where do I submit my project?', 'There are two assessment periods each year: March and September', 'Where and When can I sit my exams?', 'Find out more about the annual availability for HEQ exams. We have exam venues across the world, including Colombo and Reduit. ', 'How much does it cost?', 'Exam price: £90\r\n\r\nProject submission: £105', 'What format is the exam?', 'Three-hour ‘closed book’ per module,\r\nPass mark: 40% per module', 50, '2022-01-21 12:04:47', '2022-01-21 12:04:47', '/storage/bth.png');

-- ----------------------------
-- Table structure for page_certificates
-- ----------------------------
DROP TABLE IF EXISTS `page_certificates`;
CREATE TABLE `page_certificates`  (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 52 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

