-- ----------------------------
-- Records of page_details_accordians
-- ----------------------------
INSERT INTO `page_details_accordians` VALUES (1, 'Step 1 - Registration', 'Start your application by completing the registration form and emailing it to us at eprofessional@bcs.uk.\r\n\r\nThis provides us with your contact details, and details of the prerequisite certificates that you hold. When you’ve registered, we’ll send you an email confirming your next steps.', '', '2022-02-02 10:03:40', '2022-02-02 10:03:40', 49);
INSERT INTO `page_details_accordians` VALUES (2, 'Step 2 - Written Submission', 'Experience requirement\r\nThe experience you set out in your written submission should equate to a minimum of five years working in the field of business analysis at a senior level, as defined in the Skills Framework for the Information Age (SFIA) - you’ll be expected to have had responsibility and accountability for business analysis work at SFIA level 5, with some work at SFIA level 6.\r\n\r\n\r\nYou should provide examples of your application of business analysis using the stages of the Business Analysis Process Model:', 'Investigate situation,\nConsider perspectives,\nAnalyse needs,\nEvaluate options,\nDefine requirements', '2022-02-02 10:02:46', '2022-02-02 10:02:46', 49);
INSERT INTO `page_details_accordians` VALUES (3, 'Step 3 - Supporter Validation', '', 'You need to identify at least one supporter - an individual who know you in a professional capacity who’s able to comment on your competence and experience. Typically they’ll be in a more senior role or management position relating to the work you’ve featured in your written submission,\r\n\r\nYour supporter(s) must read and sign your written application before it’s submitted, validating the information you’ve provided and confirming their support for your application.,\r\n\r\nPlease notify your supporter(s) that BCS will hold their contact details for the purposes of processing your application and that they may be contacted by a BCS assessor if we need to discuss any of the evidence in your application in greater depth.', '2022-02-02 10:02:46', '2022-02-02 10:02:46', 49);
INSERT INTO `page_details_accordians` VALUES (4, ' Step 4 - Portfolio Review', 'BCS may decide to award the Advanced International Diploma based on your portfolio evidence alone, or we may need to discuss your evidence in more detail, with you or your supporter(s). If this is the case, an authorised BCS assessor will organise a call, and then report his recommendations to BCS so we can make our final decision.', 'your certification evidence is correct,\r\nyou’ve made a sufficient contribution to the BA community,\r\nconforms with the five-year minimum requirement and is supported by a senior colleague,\r\nis relevant business analysis work and demonstrates the appropriate SFIA levels,\r\ndemonstrates your personal skills relevant to the appropriate area: stakeholder engagement or team leadership', '2022-02-02 10:02:46', '2022-02-02 10:02:46', 49);
INSERT INTO `page_details_accordians` VALUES (5, 'Step 5 - What happens when i achieve my Advanced International Diploma?', '', 'You’re required to recertify your Advanced International Diploma every three years. This involves providing evidence of your continuing BA experience personal development and contribution to the BA community.,\r\n\r\nWhen you successfully complete the BCS Advanced International Diploma in Business Analysis you are eligible to apply to become a Chartered IT Professional.,\r\n\r\nSet yourself apart as a highly-skilled IT professional, apply for CITP.', '2022-02-02 10:02:46', '2022-02-02 10:02:46', 49);

